commits.content:
@use "../abstracts/variables";
.aboutMe{
    .about-description-card{
        background-color: #E3B6B1;
        display: flex;
        flex-direction: column;

        @media screen and (min-width:800px) {
            // display:grid ;
            // grid-template-columns: 1fr 2fr;
            display: flex;
            flex-direction: row;
            padding: 5rem 7rem 3.5rem 7rem;
            gap: 1rem;

            img{
                margin: 0 auto;
            }

            .about-text-container{
                // margin: auto 0;
            
                h3{
                    font-size: 48px;
                    width: 80%;
                }
                .btn-aboutMe-container{
                    display: flex;
                    justify-content: start;
                    margin: 0;

                    .btn-aboutMe{
                        display: flex;
                        justify-content: start;
                    }
                }
            }
            
        }

        .about-text-container{
            padding: 2rem 1rem;
            color: #725A58;
            h3{
                margin-bottom: 1rem;
            }

            .btn-aboutMe-container{
                display: flex;
                justify-content: center;

                .btn-aboutMe{
                    background-color: transparent;
                    color: #725A58;
                    font-size: 18px;
                    display: flex;
                    justify-content: center;
                    padding: 1rem 3rem;
                    border: 0.5px solid #725A58;
                    margin: 0.5rem auto;
                }
            }
        }
    }

    .brand-logo-container{
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 16px;
        margin: 1rem auto;

        @media screen and (min-width:800px) {
            grid-template-columns: 1fr 1fr 1fr 1fr;
            gap: 3rem;
        }

        .logo-container{
            display: flex;
            align-items: center;
            img{
                opacity: 30%;
                display: flex;
                justify-content: center;
                margin: 0.2rem auto;
            }
        }
    }
}
