@media screen and (min-width: 768px) {
    header {
        .desktop-header {
            display: flex;
            position: sticky;
            top: 0;

            nav {
                width: 100%;
                padding: 3rem;

                menu {
                    display: flex;
                    align-items: center;
                    justify-content: space-between;
                    gap: 1rem;

                    li {
                        list-style: none;

                        a {
                            text-decoration: none;
                            color: #725A58;
                            font-family: "Baskervville", serif;

                            &:hover {
                                text-decoration: underline 1px;
                                text-underline-offset: 2px;
                            }

                        }
                    }

                    .services {
                        a {
                            text-decoration: underline 1px;
                            text-underline-offset: 2px;
                        }
                    }

                    li:not(:last-child) {
                        border-right: 1px solid #E3B6B1;
                        padding-right: 4rem;
                    }
                }
            }

            .logo {
                flex-shrink: 0;

                img {
                    width: 100%;
                    max-width: 80px;
                    padding-top: 2rem;
                }
            }
        }

        .mobile-header {
            display: none;
        }
    }

    main {
        .title {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            gap: 3rem;
            padding: 1rem .5rem 1rem .5rem;

            h1 {
                font-size: 80px;
                font-family: "Baskervville", serif;
                color: #725A58;
                font-weight: 400;
            }

            p {
                font-size: 18px;
                font-family: "Lato", sans-serif;
                font-weight: 300;
                width: 35%;
                text-align: center;
            }
        }

        .content {
            display: flex;
            flex-direction: column;
            padding: 80px;
            gap: 40px;



            .first-content {
                display: flex;
                flex-direction: row;
                gap: 2rem;

                .description {
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;

                    h4 {
                        color: #725A58;
                        font-family: "Baskervville", serif;
                        font-size: 40px;
                        font-weight: 400;
                    }

                    .info {
                        display: flex;
                        flex-direction: row;
                        gap: 1rem;

                        .price {
                            font-family: "Lato", sans-serif;
                            font-size: 18px;
                            font-weight: 300;
                        }

                        .time {
                            font-family: "Lato", sans-serif;
                            font-size: 18px;
                            font-weight: 300;
                        }
                    }

                    h6 {
                        font-family: "Baskervville", serif;
                        font-size: 24px;
                        font-weight: 400;
                    }

                    p {
                        font-family: "Lato", sans-serif;
                        font-size: 18px;
                        font-weight: 300;
                    }

                    button {
                        font-family: "Baskervville", serif;
                        font-size: 20px;
                        font-weight: 400;
                        padding: 24px 32px 24px 32px;
                        align-self: flex-start;
                        border: 1px solid #5E4A49;
                        color: #5E4A49;
                    }
                }
            }

            .second-content {
                display: flex;
                flex-direction: row;
                gap: 2rem;


                .description {
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;

                    h4 {
                        color: #725A58;
                        font-family: "Baskervville", serif;
                        font-size: 40px;
                        font-weight: 400;
                    }

                    .info {
                        display: flex;
                        gap: 1rem;

                        .price {
                            font-family: "Lato", sans-serif;
                            font-size: 18px;
                            font-weight: 300;
                        }

                        .time {
                            font-family: "Lato", sans-serif;
                            font-size: 18px;
                            font-weight: 300;
                        }
                    }

                    h6 {
                        font-family: "Baskervville", serif;
                        font-size: 24px;
                        font-weight: 400;
                    }

                    p {
                        font-family: "Lato", sans-serif;
                        font-size: 18px;
                        font-weight: 300;
                    }

                    button {
                        font-family: "Baskervville", serif;
                        font-size: 20px;
                        font-weight: 400;
                        padding: 24px 32px 24px 32px;
                        align-self: flex-start;
                        border: 1px solid #5E4A49;
                        color: #5E4A49;
                    }
                }
            }

            .third-content {
                display: flex;
                flex-direction: row;
                gap: 2rem;


                .description {
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;

                    h4 {
                        color: #725A58;
                        font-family: "Baskervville", serif;
                        font-size: 40px;
                        font-weight: 400;
                    }

                    .info {
                        display: flex;
                        gap: 1rem;

                        .price {
                            font-family: "Lato", sans-serif;
                            font-size: 18px;
                            font-weight: 300;
                        }

                        .time {
                            font-family: "Lato", sans-serif;
                            font-size: 18px;
                            font-weight: 300;
                        }
                    }

                    h6 {
                        font-family: "Baskervville", serif;
                        font-size: 24px;
                        font-weight: 400;
                    }

                    p {
                        font-family: "Lato", sans-serif;
                        font-size: 18px;
                        font-weight: 300;
                    }

                    button {
                        font-family: "Baskervville", serif;
                        font-size: 20px;
                        font-weight: 400;
                        padding: 24px 32px 24px 32px;
                        align-self: flex-start;
                        border: 1px solid #5E4A49;
                        color: #5E4A49;
                    }
                }
            }
        }

        .fixed-contact {
            display: block;
            position: relative;

            #contact-icon {
                display: none;

                &:checked~.contact-icons {
                    display: flex;
                }

                &:checked~.open-contact {
                    background-image: url("../images/X-mark-min.png");
                }
            }

            .open-contact {
                position: fixed;
                top: calc(100vh - 120px);
                right: 0;
                width: 60px;
                height: 60px;
                background-image: url("../images/contact-icon-min.png");
            }



            .contact-icons {
                position: fixed;
                top: calc(100vh - 300px);
                right: 0;
                display: flex;
                flex-direction: column;
                display: none;

                a {
                    line-height: 0;
                }

            }
        }
    }
}
