@import url("https://fonts.googleapis.com/css2?family=Baskervville:ital,wght@0,400..700;1,400..700&family=Lato:ital,wght@0,100;0,300;0,400;0,700;0,900;1,100;1,300;1,400;1,700;1,900&display=swap");

.video-section{
    .video-sec-container{

        .video-sec-top{

            .h4-cap{
                font-size: 40px;
                font-weight: 400;
                margin: 2rem;
                color: rgba(114, 90, 88, 1);

                @media  screen and (min-width:1100px){
                    font-size: 80px;                  
                }
    
            }


            .line-section{
                width: 200px;
                margin: 0 auto;
                height: 1px;
                border-top: 1px solid rgba(227,182,177,1);
                padding-bottom: 2rem;
            }
        }

    }
}
